INSERT INTO `red_hero` (`name`,`description`,`image_url`,`alias`,`title`,`category`,`content`,`gender`,`ethnicity`,`birth_date`,`death_date`,`birthplace`,`political_status`) VALUES
                                                                                                                                                                                  ('毛泽东','伟大的马克思主义者，无产阶级革命家、战略家和理论家，中国共产党、中国人民解放军和中华人民共和国的主要创立者。','/images/hero/maozedong.jpg','润之','伟大的领袖和导师、主席','革命家','毛泽东（1893年12月26日-1976年9月9日），字润之，湖南湘潭人。中国共产党、中国人民解放军和中华人民共和国的主要创立者和领导人。他对马克思列宁主义的发展、军事理论的贡献以及对共产党的理论贡献被称为毛泽东思想。毛泽东被视为现代世界历史中最重要的人物之一，《时代》杂志将他评为20世纪最具影响100人之一。','男','汉族','1893-12-26','1976-09-09','湖南省湘潭县','中共党员'),
                                                                                                                                                                                  ('周恩来','伟大的无产阶级革命家、政治家、军事家、外交家，党和国家主要领导人之一，中国人民解放军主要创建人之一。','/images/hero/zhouenlai.jpg','翔宇','总理、外交家','革命家','周恩来（1898年3月5日-1976年1月8日），字翔宇，原籍浙江绍兴，生于江苏淮安。中国共产党的主要领导人之一，中华人民共和国开国总理，是党和国家的卓越领导人。他是以毛泽东同志为核心的党的第一代中央领导集体的重要成员，在国际上也享有很高声誉。','男','汉族','1898-03-05','1976-01-08','浙江省绍兴市','中共党员'),
                                                                                                                                                                                  ('邓小平','中国社会主义改革开放和现代化建设的总设计师，中国特色社会主义道路的开创者。','/images/hero/dengxiaoping.jpg','小平','总设计师、改革开放总设计师','革命家','邓小平（1904年8月22日-1997年2月19日），四川广安人。中国共产党、中国人民解放军、中华人民共和国的主要领导人之一。他是中国社会主义改革开放和现代化建设的总设计师，中国特色社会主义道路的开创者，邓小平理论的主要创立者。','男','汉族','1904-08-22','1997-02-19','四川省广安县','中共党员'),
                                                                                                                                                                                  ('朱德','伟大的无产阶级革命家、政治家、军事家，中国人民解放军的主要缔造者之一，中华人民共和国的开国元勋。','/images/hero/zhude.jpg','玉阶','总司令、元帅','军事家','朱德（1886年12月1日-1976年7月6日），字玉阶，四川仪陇人。中国共产党、中国人民解放军和中华人民共和国的主要领导人之一。中华人民共和国十大元帅之首，伟大的无产阶级革命家、军事家、政治家。','男','汉族','1886-12-01','1976-07-06','四川省仪陇县','中共党员'),
                                                                                                                                                                                  ('彭德怀','中华人民共和国开国元帅，无产阶级革命家、军事家，中国人民解放军的创建人和领导人之一。','/images/hero/pengdehuai.jpg','得华','元帅、大将军','军事家','彭德怀（1898年10月24日-1974年11月29日），湖南湘潭人。中国共产党、中国人民解放军和中华人民共和国的重要领导人之一。中华人民共和国十大元帅之一，曾任国防部长，抗美援朝战争中国人民志愿军司令员。','男','汉族','1898-10-24','1974-11-29','湖南省湘潭县','中共党员');
INSERT INTO party_encyclopedia (title, content, image_url) VALUES
                                                               ('五四运动', '1919年5月4日发生在北京的一场以青年学生为主，广大群众、市民、工商人士等阶层共同参与的爱国运动，是中国人民彻底的反对帝国主义、封建主义的爱国运动。', '/images/encyclopedia/may4th.jpg'),
                                                               ('中国共产党成立', '1921年7月23日，中国共产党第一次全国代表大会在上海召开，宣告了中国共产党的正式成立，这是中国历史上开天辟地的大事变。', '/images/encyclopedia/founding.jpg'),
                                                               ('南昌起义', '1927年8月1日，中国共产党领导部分国民革命军在江西省南昌市举行的武装起义，打响了武装反抗国民党反动派的第一枪。', '/images/encyclopedia/nanchang.jpg'),
                                                               ('长征', '1934年10月至1936年10月，中国工农红军主力从长江以南各革命根据地向陕甘革命根据地进行的战略转移。', '/images/encyclopedia/longmarch.jpg'),
                                                               ('开国大典', '1949年10月1日，在北京天安门广场举行的中华人民共和国中央人民政府成立典礼，毛泽东主席向全世界庄严宣告中华人民共和国成立。', '/images/encyclopedia/founding_ceremony.jpg');
INSERT INTO red_scenic_spot (name, description, location, image_url) VALUES
                                                                         ('延安革命纪念馆', '位于陕西省延安市，全面展示了中共中央在延安十三年的革命历史，是重要的爱国主义教育基地。', '陕西省延安市', '/images/scenic/yanan.jpg'),
                                                                         ('井冈山革命根据地', '中国第一个农村革命根据地，被誉为"中国革命的摇篮"，是中国工农红军创建的地方。', '江西省井冈山市', '/images/scenic/jinggangshan.jpg'),
                                                                         ('西柏坡纪念馆', '位于河北省平山县，是解放战争时期中共中央所在地，新中国从这里走来。', '河北省平山县', '/images/scenic/xibaipo.jpg'),
                                                                         ('遵义会议会址', '1935年1月中共中央政治局在贵州遵义召开的扩大会议会址，这次会议在极端危急的历史关头挽救了党和红军。', '贵州省遵义市', '/images/scenic/zunyi.jpg'),
                                                                         ('中国人民抗日战争纪念馆', '位于北京卢沟桥畔，全面展示了中国人民抗日战争的伟大历史。', '北京市丰台区', '/images/scenic/war_museum.jpg');
INSERT INTO `red_story` (`title`,`content`,`source`,`image_url`,`summary`,`story_time`,`location`,`hero_name`,`created_at`) VALUES
                                                                                                                                ('半条被子的故事','长征途中，3名女红军借宿湖南汝城县沙洲村徐解秀老人家中，临走时，把自己仅有的一床被子剪下一半给老人留下了。老人说，什么是共产党？共产党就是自己有一条被子，也要剪下半条给老百姓的人。','《人民日报》','/images/stories/half-quilt.jpg','长征中女红军与老百姓鱼水情深的感人故事','1934年11月','湖南省汝城县沙洲村','三名女红军','2023-10-01 09:00:00'),
                                                                                                                                ('金色的鱼钩','长征过草地途中，一位炊事班长为了照顾三个生病的小战士，用缝衣针做成鱼钩钓鱼给他们补充营养，自己却因饥饿和劳累牺牲在草地边缘。','小学语文课文','/images/stories/golden-fishhook.jpg','长征途中老班长舍己为人的感人故事','1935年8月','川西北草地','老班长','2023-10-02 10:30:00'),('董存瑞炸碉堡','1948年5月25日，在解放隆化的战斗中，董存瑞手托炸药包，炸毁敌人碉堡，英勇牺牲，年仅19岁。','《解放军报》','/images/stories/dong-cunrui.jpg','战斗英雄董存瑞舍身炸碉堡的英雄事迹','1948年5月25日','河北省隆化县','董存瑞','2023-10-03 14:15:00'),
                                                                                                                                ('狼牙山五壮士','1941年9月25日，八路军晋察冀军区五位战士为掩护主力部队和群众转移，将日伪军引向狼牙山绝境，最后宁死不屈，跳下悬崖。','历史资料','/images/stories/wolf-tooth-mountain.jpg','五位八路军战士宁死不屈的英雄壮举','1941年9月25日','河北省易县狼牙山','马宝玉、葛振林、宋学义、胡德林、胡福才','2023-10-04 16:45:00'),
                                                                                                                                ('小英雄雨来','抗日战争时期，晋察冀边区北部的一个小村庄，少年雨来为了掩护交通员李大叔，机智勇敢地与日本鬼子周旋的故事。','小说《小英雄雨来》','/images/stories/yu-lai.jpg','抗日小英雄雨来机智勇敢的故事','抗日战争时期','晋察冀边区','雨来','2023-10-05 11:20:00'),
                                                                                                                                ('八女投江','1938年10月，以冷云为首的东北抗日联军8名女官兵，在顽强抗击日本侵略军的战斗中弹尽援绝，毅然投入滚滚江水，为国捐躯。','《东北抗日联军史》','/images/stories/eight-women.jpg','八位女战士宁死不屈投江殉国的英勇事迹','1938年10月','黑龙江省林口县乌斯浑河','冷云、杨贵珍、安顺福、胡秀芝、郭桂琴、黄桂清、王惠民、李凤善','2023-10-06 13:10:00'),
                                                                                                                                ('黄继光堵枪眼','1952年10月19日，在朝鲜上甘岭战役中，黄继光用胸膛堵住敌人地堡的机枪射孔，为部队冲锋开辟道路，壮烈牺牲。','《志愿军英雄传》','/images/stories/huang-jiguang.jpg','特级英雄黄继光用身体堵枪眼的英雄壮举','1952年10月19日','朝鲜上甘岭','黄继光','2023-10-07 15:30:00'),
                                                                                                                                ('刘胡兰的故事','1947年1月12日，年仅15岁的刘胡兰面对敌人的铡刀毫不畏惧，坚贞不屈，大义凛然，壮烈牺牲。毛泽东为她题词"生的伟大，死的光荣"。','《革命烈士传》','/images/stories/liu-hulan.jpg','少年女英雄刘胡兰宁死不屈的革命精神','1947年1月12日','山西省文水县云周西村','刘胡兰','2023-10-08 09:45:00');
INSERT INTO red_explore (title, image, city, start_time, end_time, location, max_participants, status, organization, registration_email, registration_form, description, schedule, registration_deadline) VALUES
                                                                                                                                                                                                              ('重走长征路——湘江战役遗址探访', '/images/explore/xiangjiang.jpg', '桂林市', '2023-09-15 09:00:00', '2023-09-17 18:00:00', '广西兴安、全州湘江战役遗址', 40, 'ended', '广西革命历史研究会', 'xiangjiang@example.com', '/docs/registration_xiangjiang.pdf', '走访广西兴安、全州等地，探寻湘江战役的历史遗迹，缅怀革命先烈的英勇事迹。', '{"day1":["09:00 开幕式","10:30 纪念馆参观"]}', '2023-09-10'),
                                                                                                                                                                                                              ('探访大别山革命老区', '/images/explore/dabieshan.jpg', '黄冈市', '2023-09-20 08:00:00', '2023-09-22 17:00:00', '湖北大别山革命老区', 50, 'ended', '湖北省党史研究室', 'dabieshan@example.com', '/docs/registration_dabieshan.pdf', '深入大别山革命老区，了解刘邓大军千里跃进大别山的历史，感受老区人民的新生活。', '{"day1":["08:00 集合出发","14:00 老区参观"]}', '2023-09-15'),
                                                                                                                                                                                                              ('追寻红色记忆——上海一大会址参观', '/images/explore/shanghai.jpg', '上海市', '2023-09-25 09:30:00', '2023-09-25 16:00:00', '上海市黄浦区', 60, 'ended', '上海市党史办', 'shanghai@example.com', '/docs/registration_shanghai.pdf', '参观中共一大会址纪念馆，了解中国共产党诞生的伟大历程。', '{"day1":["09:30 纪念馆参观","14:00 专题学习"]}', '2023-09-20'),
                                                                                                                                                                                                              ('延安精神学习之旅', '/images/explore/yanan.jpg', '延安市', '2023-10-05 10:00:00', '2023-10-07 18:00:00', '陕西省延安市', 45, 'ended', '延安干部学院', 'yanan@example.com', '/docs/registration_yanan.pdf', '在延安参观革命旧址，学习延安精神，感受艰苦奋斗的革命传统。', '{"day1":["10:00 开班式","14:00 宝塔山"]}', '2023-09-30'),
                                                                                                                                                                                                              ('抗美援朝纪念馆参观', '/images/explore/kangmeiyuanchao.jpg', '丹东市', '2023-10-10 08:30:00', '2023-10-11 17:00:00', '辽宁省丹东市', 55, 'ended', '丹东市委宣传部', 'dandong@example.com', '/docs/registration_dandong.pdf', '参观丹东抗美援朝纪念馆，了解中国人民志愿军的英勇事迹和伟大抗美援朝精神。', '{"day1":["08:30 纪念馆参观","14:00 鸭绿江断桥"]}', '2023-10-05');
